 */
function $MODULE$_entity_delete(Drupal\Core\Entity\EntityInterface $entity) {

  // Delete the entity's entry from a fictional table of all entities.
  \Drupal::database()
    ->delete('example_entity')
    ->condition('type', $entity
    ->getEntityTypeId())
    ->condition('id', $entity
    ->id())
    ->execute();
}

/**
 * Implements hook_entity_display_build_alter().
 */
function $MODULE$_entity_display_build_alter(&$build, $context) {

  // Append RDF term mappings on displayed taxonomy links.
  foreach (Element::children($build) as $field_name) {
    $element =& $build[$field_name];
    if ($element['#field_type'] == 'entity_reference' && $element['#formatter'] == 'entity_reference_label') {
      foreach ($element['#items'] as $delta => $item) {
        $term = $item->entity;
        if (!empty($term->rdf_mapping['rdftype'])) {
          $element[$delta]['#options']['attributes']['typeof'] = $term->rdf_mapping['rdftype'];
        }
        if (!empty($term->rdf_mapping['name']['predicates'])) {
          $element[$delta]['#options']['attributes']['property'] = $term->rdf_mapping['name']['predicates'];
        }
      }
    }
  }
}

/**
 * Implements hook_entity_extra_field_info_alter().
 */
function hook_entity_extra_field_info_alter(&$info) {

  // Force node title to always be at the top of the list by default.
  foreach (NodeType::loadMultiple() as $bundle) {
    if (isset($info['node'][$bundle
      ->id()]['form']['title'])) {
      $info['node'][$bundle
        ->id()]['form']['title']['weight'] = -20;
    }
  }
}

/**
 * Implements hook_entity_field_access().
 */
function $MODULE$_entity_field_access($operation, \Drupal\Core\Field\FieldDefinitionInterface $field_definition, \Drupal\Core\Session\AccountInterface $account, \Drupal\Core\Field\FieldItemListInterface $items = NULL) {
  if ($field_definition
    ->getName() == 'field_of_interest' && $operation == 'edit') {
    return AccessResult::allowedIfHasPermission($account, 'update field of interest');
  }
  return AccessResult::neutral();
}

/**
 * Implements hook_entity_field_access_alter().
 */
function $MODULE$_entity_field_access_alter(array &$grants, array $context) {

  /** @var \Drupal\Core\Field\FieldDefinitionInterface $field_definition */
  $field_definition = $context['field_definition'];
  if ($field_definition
    ->getName() == 'field_of_interest' && $grants['node']
    ->isForbidden()) {

    // Override node module's restriction to no opinion (neither allowed nor
    // forbidden). We don't want to provide our own access hook, we only want to
    // take out node module's part in the access handling of this field. We also
    // don't want to switch node module's grant to
    // AccessResultInterface::isAllowed() , because the grants of other modules
    // should still decide on their own if this field is accessible or not
    $grants['node'] = AccessResult::neutral()
      ->inheritCacheability($grants['node']);
  }
}

/**
 * Implements hook_entity_field_storage_info().
 */
function $MODULE$_entity_field_storage_info(\Drupal\Core\Entity\EntityTypeInterface $entity_type) {
  if (\Drupal::entityTypeManager()
    ->getStorage($entity_type
    ->id()) instanceof DynamicallyFieldableEntityStorageInterface) {

    // Query by filtering on the ID as this is more efficient than filtering
    // on the entity_type property directly.
    $ids = \Drupal::entityQuery('field_storage_config')
      ->condition('id', $entity_type
      ->id() . '.', 'STARTS_WITH')
      ->execute();

    // Fetch all fields and key them by field name.
    $field_storages = FieldStorageConfig::loadMultiple($ids);
    $result = [];
    foreach ($field_storages as $field_storage) {
      $result[$field_storage
        ->getName()] = $field_storage;
    }
    return $result;
  }
}

/**
 * Implements hook_entity_field_storage_info_alter().
 */
function $MODULE$_entity_field_storage_info_alter(&$fields, \Drupal\Core\Entity\EntityTypeInterface $entity_type) {

  // Alter the max_length setting.
  if ($entity_type
    ->id() == 'node' && !empty($fields['mymodule_text'])) {
    $fields['mymodule_text']
      ->setSetting('max_length', 128);
  }
}

