/**
 * Implements hook_entity_predelete().
 */
function $MODULE$_entity_predelete(Drupal\Core\Entity\EntityInterface $entity) {
  $connection = \Drupal::database();

  // Count references to this entity in a custom table before they are removed
  // upon entity deletion.
  $id = $entity
    ->id();
  $type = $entity
    ->getEntityTypeId();
  $count = \Drupal::database()
    ->select('example_entity_data')
    ->condition('type', $type)
    ->condition('id', $id)
    ->countQuery()
    ->execute()
    ->fetchField();

  // Log the count in a table that records this statistic for deleted entities.
  $connection
    ->merge('example_deleted_entity_statistics')
    ->key([
    'type' => $type,
    'id' => $id,
  ])
    ->fields([
    'count' => $count,
  ])
    ->execute();
}

/**
 * Implements hook_entity_preload().
 */
function $MODULE$_entity_preload(array $ids, $entity_type_id) {
  $entities = [];
  foreach ($ids as $id) {
    $entities[] = mymodule_swap_revision($id);
  }
  return $entities;
}

/**
 * Implements hook_entity_prepare_form().
 */
function $MODULE$_entity_prepare_form(\Drupal\Core\Entity\EntityInterface $entity, $operation, \Drupal\Core\Form\FormStateInterface $form_state) {
  if ($operation == 'edit') {
    $entity->label->value = 'Altered label';
    $form_state
      ->set('label_altered', TRUE);
  }
}

/**
 * Implements hook_entity_prepare_view().
 */
function $MODULE$_entity_prepare_view($entity_type_id, array $entities, array $displays, $view_mode) {

  // Load a specific node into the user object for later theming.
  if (!empty($entities) && $entity_type_id == 'user') {

    // Only do the extra work if the component is configured to be
    // displayed. This assumes a 'mymodule_addition' extra field has been
    // defined for the entity bundle in hook_entity_extra_field_info().
    $ids = [];
    foreach ($entities as $id => $entity) {
      if ($displays[$entity
        ->bundle()]
        ->getComponent('mymodule_addition')) {
        $ids[] = $id;
      }
    }
    if ($ids) {
      $nodes = mymodule_get_user_nodes($ids);
      foreach ($ids as $id) {
        $entities[$id]->user_node = $nodes[$id];
      }
    }
  }
}

/**
 * Implements hook_entity_presave().
 */
function $MODULE$_entity_presave(Drupal\Core\Entity\EntityInterface $entity) {
  if ($entity instanceof ContentEntityInterface && $entity
    ->isTranslatable()) {
    $route_match = \Drupal::routeMatch();
    \Drupal::service('content_translation.synchronizer')
      ->synchronizeFields($entity, $entity
      ->language()
      ->getId(), $route_match
      ->getParameter('source_langcode'));
  }
}

/**
 * Implements hook_entity_revision_create().
 */
function $MODULE$_entity_revision_create(Drupal\Core\Entity\EntityInterface $new_revision, Drupal\Core\Entity\EntityInterface $entity, $keep_untranslatable_fields) {

  // Retain the value from an untranslatable field, which are by default
  // synchronized from the default revision.
  $new_revision
    ->set('untranslatable_field', $entity
    ->get('untranslatable_field'));
}

/**
 * Implements hook_entity_revision_delete().
 */
function $MODULE$_entity_revision_delete(Drupal\Core\Entity\EntityInterface $entity) {
  $referenced_files_by_field = _editor_get_file_uuids_by_field($entity);
  foreach ($referenced_files_by_field as $field => $uuids) {
    _editor_delete_file_usage($uuids, $entity, 1);
  }
}

