    ->execute();
}

/**
 * Implements hook_field_storage_config_update_forbid().
 */
function $MODULE$_field_storage_config_update_forbid(\Drupal\field\FieldStorageConfigInterface $field_storage, \Drupal\field\FieldStorageConfigInterface $prior_field_storage) {
  if ($field_storage
    ->getTypeProvider() == 'options' && $field_storage
    ->hasData()) {

    // Forbid any update that removes allowed values with actual data.
    $allowed_values = $field_storage
      ->getSetting('allowed_values');
    $prior_allowed_values = $prior_field_storage
      ->getSetting('allowed_values');
    $lost_keys = array_keys(array_diff_key($prior_allowed_values, $allowed_values));
    if (_options_values_in_use($field_storage
      ->getTargetEntityTypeId(), $field_storage
      ->getName(), $lost_keys)) {
      throw new \Drupal\Core\Entity\Exception\FieldStorageDefinitionUpdateForbiddenException("A list field '{$field_storage->getName()}' with existing data cannot have its keys changed.");
    }
  }
}

/**
 * Implements hook_field_ui_preconfigured_options_alter().
 */
function $MODULE$_field_ui_preconfigured_options_alter(array &$options, $field_type) {

  // If the field is not an "entity_reference"-based field, bail out.

  /** @var \Drupal\Core\Field\FieldTypePluginManager $field_type_manager */
  $field_type_manager = \Drupal::service('plugin.manager.field.field_type');
  $class = $field_type_manager
    ->getPluginClass($field_type);
  if (!is_a($class, 'Drupal\\Core\\Field\\Plugin\\Field\\FieldType\\EntityReferenceItem', TRUE)) {
    return;
  }

  // Set the default formatter for media in entity reference fields to be the
  // "Rendered entity" formatter.
  if (!empty($options['media'])) {
    $options['media']['entity_view_display']['type'] = 'entity_reference_entity_view';
  }
}

/**
 * Implements hook_field_views_data().
 */
function $MODULE$_field_views_data(\Drupal\field\FieldStorageConfigInterface $field_storage) {
  $data = views_field_default_views_data($field_storage);
  foreach ($data as $table_name => $table_data) {

    // Add the relationship only on the target_id field.
    $data[$table_name][$field_storage
      ->getName() . '_target_id']['relationship'] = [
      'id' => 'standard',
      'base' => 'file_managed',
      'base field' => 'target_id',
      'label' => t('image from @field_name', [
        '@field_name' => $field_storage
          ->getName(),
      ]),
    ];
  }
  return $data;
}

/**
 * Implements hook_field_views_data_alter().
 */
function $MODULE$_field_views_data_alter(array &$data, \Drupal\field\FieldStorageConfigInterface $field_storage) {
  $entity_type_id = $field_storage
    ->getTargetEntityTypeId();
  $field_name = $field_storage
    ->getName();
  $entity_type = \Drupal::entityTypeManager()
    ->getDefinition($entity_type_id);
  $pseudo_field_name = 'reverse_' . $field_name . '_' . $entity_type_id;
  $table_mapping = \Drupal::entityTypeManager()
    ->getStorage($entity_type_id)
    ->getTableMapping();
  list($label) = views_entity_field_label($entity_type_id, $field_name);
  $data['file_managed'][$pseudo_field_name]['relationship'] = [
    'title' => t('@entity using @field', [
      '@entity' => $entity_type
        ->getLabel(),
      '@field' => $label,
    ]),
    'help' => t('Relate each @entity with a @field set to the image.', [
      '@entity' => $entity_type
        ->getLabel(),
      '@field' => $label,
    ]),
    'id' => 'entity_reverse',
    'field_name' => $field_name,
    'entity_type' => $entity_type_id,
    'field table' => $table_mapping
      ->getDedicatedDataTableName($field_storage),
    'field field' => $field_name . '_target_id',
    'base' => $entity_type
      ->getBaseTable(),
    'base field' => $entity_type
      ->getKey('id'),
    'label' => $field_name,
    'join_extra' => [
      0 => [
        'field' => 'deleted',
        'value' => 0,
        'numeric' => TRUE,
      ],
    ],
  ];
}

/**
 * Implements hook_field_views_data_views_data_alter().
 */
function $MODULE$_field_views_data_views_data_alter(array &$data, \Drupal\field\FieldStorageConfigInterface $field) {
  $field_name = $field
    ->getName();
  $data_key = 'field_data_' . $field_name;
  $entity_type_id = $field->entity_type;
  $entity_type = \Drupal::entityTypeManager()
    ->getDefinition($entity_type_id);
  $pseudo_field_name = 'reverse_' . $field_name . '_' . $entity_type_id;
  list($label) = views_entity_field_label($entity_type_id, $field_name);
  $table_mapping = \Drupal::entityTypeManager()
    ->getStorage($entity_type_id)
    ->getTableMapping();

  // Views data for this field is in $data[$data_key].
  $data[$data_key][$pseudo_field_name]['relationship'] = [
    'title' => t('@entity using @field', [
      '@entity' => $entity_type
        ->getLabel(),
      '@field' => $label,
    ]),
    'help' => t('Relate each @entity with a @field set to the term.', [
      '@entity' => $entity_type
        ->getLabel(),
      '@field' => $label,
    ]),
    'id' => 'entity_reverse',
    'field_name' => $field_name,
    'entity_type' => $entity_type_id,
    'field table' => $table_mapping
      ->getDedicatedDataTableName($field),
    'field field' => $field_name . '_target_id',
    'base' => $entity_type
      ->getBaseTable(),
    'base field' => $entity_type
      ->getKey('id'),
    'label' => $field_name,
    'join_extra' => [
      0 => [
        'field' => 'deleted',
        'value' => 0,
        'numeric' => TRUE,
      ],
    ],
  ];
}

/**
 * Implements hook_field_widget_form_alter().
 */
function $MODULE$_field_widget_form_alter(&$element, \Drupal\Core\Form\FormStateInterface $form_state, $context) {

  // Add a css class to widget form elements for all fields of type my_type.
  $field_definition = $context['items']
    ->getFieldDefinition();
  if ($field_definition
    ->getType() == 'my_type') {

    // Be sure not to overwrite existing attributes.
    $element['#attributes']['class'][] = 'my-class';
  }
}

