  $info['sftp'] = [
    'title' => t('SFTP (Secure FTP)'),
    'class' => 'Drupal\\Core\\FileTransfer\\SFTP',
    'weight' => 10,
  ];
  return $info;
}

/**
 * Implements hook_filetransfer_info_alter().
 */
function $MODULE$_filetransfer_info_alter(&$filetransfer_info) {

  // Remove the FTP option entirely.
  unset($filetransfer_info['ftp']);

  // Make sure the SSH option is listed first.
  $filetransfer_info['ssh']['weight'] = -10;
}

/**
 * Implements hook_file_copy().
 */
function $MODULE$_file_copy(Drupal\file\FileInterface $file, Drupal\file\FileInterface $source) {

  // Make sure that the file name starts with the owner's user name.
  if (strpos($file
    ->getFilename(), $file
    ->getOwner()->name) !== 0) {
    $file
      ->setFilename($file
      ->getOwner()->name . '_' . $file
      ->getFilename());
    $file
      ->save();
    \Drupal::logger('file')
      ->notice('Copied file %source has been renamed to %destination', [
      '%source' => $source->filename,
      '%destination' => $file
        ->getFilename(),
    ]);
  }
}

/**
 * Implements hook_file_download().
 */
function $MODULE$_file_download($uri) {

  // Check to see if this is a config download.
  $scheme = StreamWrapperManager::getScheme($uri);
  $target = StreamWrapperManager::getTarget($uri);
  if ($scheme == 'temporary' && $target == 'config.tar.gz') {
    return [
      'Content-disposition' => 'attachment; filename="config.tar.gz"',
    ];
  }
}

/**
 * Implements hook_file_mimetype_mapping_alter().
 */
function $MODULE$_file_mimetype_mapping_alter(&$mapping) {

  // Add new MIME type 'drupal/info'.
  $mapping['mimetypes']['example_info'] = 'drupal/info';

  // Add new extension '.info.yml' and map it to the 'drupal/info' MIME type.
  $mapping['extensions']['info'] = 'example_info';

  // Override existing extension mapping for '.ogg' files.
  $mapping['extensions']['ogg'] = 189;
}

/**
 * Implements hook_file_move().
 */
function $MODULE$_file_move(Drupal\file\FileInterface $file, Drupal\file\FileInterface $source) {

  // Make sure that the file name starts with the owner's user name.
  if (strpos($file
    ->getFilename(), $file
    ->getOwner()->name) !== 0) {
    $file
      ->setFilename($file
      ->getOwner()->name . '_' . $file
      ->getFilename());
    $file
      ->save();
    \Drupal::logger('file')
      ->notice('Moved file %source has been renamed to %destination', [
      '%source' => $source->filename,
      '%destination' => $file
        ->getFilename(),
    ]);
  }
}

/**
 * Implements hook_file_url_alter().
 */
function $MODULE$_file_url_alter(&$uri) {
  $user = \Drupal::currentUser();

  // User 1 will always see the local file in this example.
  if ($user
    ->id() == 1) {
    return;
  }
  $cdn1 = 'http://cdn1.example.com';
  $cdn2 = 'http://cdn2.example.com';
  $cdn_extensions = [
    'css',
    'js',
    'gif',
    'jpg',
    'jpeg',
    'png',
  ];

  // Most CDNs don't support private file transfers without a lot of hassle,
  // so don't support this in the common case.
  $schemes = [
    'public',
  ];

  /** @var \Drupal\Core\StreamWrapper\StreamWrapperManagerInterface $stream_wrapper_manager */
  $stream_wrapper_manager = \Drupal::service('stream_wrapper_manager');
  $scheme = $stream_wrapper_manager::getScheme($uri);

  // Only serve shipped files and public created files from the CDN.
  if (!$scheme || in_array($scheme, $schemes)) {

    // Shipped files.
    if (!$scheme) {
      $path = $uri;
    }
    else {
      $wrapper = $stream_wrapper_manager
        ->getViaScheme($scheme);
      $path = $wrapper
        ->getDirectoryPath() . '/' . $stream_wrapper_manager::getTarget($uri);
    }

    // Clean up Windows paths.
    $path = str_replace('\\', '/', $path);

    // Serve files with one of the CDN extensions from CDN 1, all others from
    // CDN 2.
    $pathinfo = pathinfo($path);
    if (isset($pathinfo['extension']) && in_array($pathinfo['extension'], $cdn_extensions)) {
      $uri = $cdn1 . '/' . $path;
    }
    else {
      $uri = $cdn2 . '/' . $path;
    }
  }
}

/**
 * Implements hook_file_validate().
 */
function $MODULE$_file_validate(Drupal\file\FileInterface $file) {
  $errors = [];
  if (!$file
    ->getFilename()) {
    $errors[] = t("The file's name is empty. Please give a name to the file.");
  }
  if (strlen($file
    ->getFilename()) > 255) {
    $errors[] = t("The file's name exceeds the 255 characters limit. Please rename the file and try again.");
  }
  return $errors;
}

