/**
 * Implements hook_migrate_MIGRATION_ID_prepare_row().
 */
function $MODULE$_migrate_$MIGRATION_ID$_prepare_row(Row $row, MigrateSourceInterface $source, MigrationInterface $migration) {
  $value = $source
    ->getDatabase()
    ->query('SELECT value FROM {variable} WHERE name = :name', [
    ':name' => 'mymodule_filter_foo_' . $row
      ->getSourceProperty('format'),
  ])
    ->fetchField();
  if ($value) {
    $row
      ->setSourceProperty('settings:mymodule:foo', unserialize($value));
  }
}

/**
 * Implements hook_migrate_prepare_row().
 */
function $MODULE$_migrate_prepare_row(Row $row, MigrateSourceInterface $source, MigrationInterface $migration) {
  if ($migration
    ->id() == 'd6_filter_formats') {
    $value = $source
      ->getDatabase()
      ->query('SELECT value FROM {variable} WHERE name = :name', [
      ':name' => 'mymodule_filter_foo_' . $row
        ->getSourceProperty('format'),
    ])
      ->fetchField();
    if ($value) {
      $row
        ->setSourceProperty('settings:mymodule:foo', unserialize($value));
    }
  }
}

/**
 * Implements hook_migration_plugins_alter().
 */
function $MODULE$_migration_plugins_alter(array &$migrations) {
  $migrations = array_filter($migrations, function (array $migration) {
    $tags = isset($migration['migration_tags']) ? (array) $migration['migration_tags'] : [];
    return !in_array('Drupal 6', $tags);
  });
}

/**
 * Implements hook_modules_installed().
 */
function $MODULE$_modules_installed($modules, $is_syncing) {
  if (in_array('lousy_module', $modules)) {
    \Drupal::state()
      ->set('mymodule.lousy_module_compatibility', TRUE);
  }
  if (!$is_syncing) {
    \Drupal::service('mymodule.service')
      ->doSomething($modules);
  }
}

/**
 * Implements hook_modules_uninstalled().
 */
function $MODULE$_modules_uninstalled($modules, $is_syncing) {
  if (in_array('lousy_module', $modules)) {
    \Drupal::state()
      ->delete('mymodule.lousy_module_compatibility');
  }
  mymodule_cache_rebuild();
  if (!$is_syncing) {
    \Drupal::service('mymodule.service')
      ->doSomething($modules);
  }
}

/**
 * Implements hook_module_implements_alter().
 */
function $MODULE$_module_implements_alter(&$implementations, $hook) {
  if ($hook == 'form_alter') {

    // Move my_module_form_alter() to the end of the list.
    // \Drupal::moduleHandler()->getImplementations()
    // iterates through $implementations with a foreach loop which PHP iterates
    // in the order that the items were added, so to move an item to the end of
    // the array, we remove it and then add it.
    $group = $implementations['my_module'];
    unset($implementations['my_module']);
    $implementations['my_module'] = $group;
  }
}

/**
 * Implements hook_module_preinstall().
 */
function $MODULE$_module_preinstall($module) {
  mymodule_cache_clear();
}

/**
 * Implements hook_module_preuninstall().
 */
function $MODULE$_module_preuninstall($module) {
  mymodule_cache_clear();
}

/**
 * Implements hook_node_access_records().
 */
function $MODULE$_node_access_records(\Drupal\node\NodeInterface $node) {

  // We only care about the node if it has been marked private. If not, it is
  // treated just like any other node and we completely ignore it.
  if ($node->private->value) {
    $grants = [];

    // Only published Catalan translations of private nodes should be viewable
    // to all users. If we fail to check $node->isPublished(), all users would be able
    // to view an unpublished node.
    if ($node
      ->isPublished()) {
      $grants[] = [
        'realm' => 'example',
        'gid' => 1,
        'grant_view' => 1,
        'grant_update' => 0,
        'grant_delete' => 0,
        'langcode' => 'ca',
      ];
    }

    // For the example_author array, the GID is equivalent to a UID, which
    // means there are many groups of just 1 user.
    // Note that an author can always view nodes they own, even if they have
    // status unpublished.
    if ($node
      ->getOwnerId()) {
      $grants[] = [
        'realm' => 'example_author',
        'gid' => $node
          ->getOwnerId(),
        'grant_view' => 1,
        'grant_update' => 1,
        'grant_delete' => 1,
        'langcode' => 'ca',
      ];
    }
    return $grants;
  }
}

/**
 * Implements hook_node_access_records_alter().
 */
function $MODULE$_node_access_records_alter(&$grants, Drupal\node\NodeInterface $node) {

  // Our module allows editors to mark specific articles with the 'is_preview'
  // field. If the node being saved has a TRUE value for that field, then only
  // our grants are retained, and other grants are removed. Doing so ensures
  // that our rules are enforced no matter what priority other grants are given.
  if ($node->is_preview) {

    // Our module grants are set in $grants['example'].
    $temp = $grants['example'];

    // Now remove all module grants but our own.
    $grants = [
      'example' => $temp,
    ];
  }
}

/**
 * Implements hook_node_grants().
 */
function $MODULE$_node_grants(\Drupal\Core\Session\AccountInterface $account, $op) {
  if ($account
    ->hasPermission('access private content')) {
    $grants['example'] = [
      1,
    ];
  }
  if ($account
    ->id()) {
    $grants['example_author'] = [
      $account
        ->id(),
    ];
  }
  return $grants;
}

/**
 * Implements hook_node_grants_alter().
 */
function $MODULE$_node_grants_alter(&$grants, \Drupal\Core\Session\AccountInterface $account, $op) {

  // Our sample module never allows certain roles to edit or delete
  // content. Since some other node access modules might allow this
  // permission, we expressly remove it by returning an empty $grants
  // array for roles specified in our variable setting.
  // Get our list of banned roles.
  $restricted = \Drupal::config('example.settings')
    ->get('restricted_roles');
  if ($op != 'view' && !empty($restricted)) {

    // Now check the roles for this account against the restrictions.
    foreach ($account
      ->getRoles() as $rid) {
      if (in_array($rid, $restricted)) {
        $grants = [];
      }
    }
  }
}

