  unset($definitions['help_block']);
}

/**
 * Implements hook_post_update_NAME().
 */
function $MODULE$_post_update_$NAME$(&$sandbox) {

  // Example of updating some content.
  $node = \Drupal\node\Entity\Node::load(123);
  $node
    ->setTitle('foo');
  $node
    ->save();
  $result = t('Node %nid saved', [
    '%nid' => $node
      ->id(),
  ]);

  // Example of updating some config.
  if (\Drupal::moduleHandler()
    ->moduleExists('taxonomy')) {

    // Update the dependencies of all Vocabulary configuration entities.
    \Drupal::classResolver(\Drupal\Core\Config\Entity\ConfigEntityUpdater::class)
      ->update($sandbox, 'taxonomy_vocabulary');
  }
  return $result;
}

/**
 * Implements hook_preprocess().
 */
function $MODULE$_preprocess(&$variables, $hook) {
  static $hooks;

  // Add contextual links to the variables, if the user has permission.
  if (!\Drupal::currentUser()
    ->hasPermission('access contextual links')) {
    return;
  }
  if (!isset($hooks)) {
    $hooks = theme_get_registry();
  }

  // Determine the primary theme function argument.
  if (isset($hooks[$hook]['variables'])) {
    $keys = array_keys($hooks[$hook]['variables']);
    $key = $keys[0];
  }
  else {
    $key = $hooks[$hook]['render element'];
  }
  if (isset($variables[$key])) {
    $element = $variables[$key];
  }
  if (isset($element) && is_array($element) && !empty($element['#contextual_links'])) {
    $variables['title_suffix']['contextual_links'] = contextual_links_view($element);
    if (!empty($variables['title_suffix']['contextual_links'])) {
      $variables['attributes']['class'][] = 'contextual-links-region';
    }
  }
}

/**
 * Implements hook_preprocess_HOOK().
 */
function $MODULE$_preprocess_$HOOK$(&$variables) {

  // This example is from rdf_preprocess_image(). It adds an RDF attribute
  // to the image hook's variables.
  $variables['attributes']['typeof'] = [
    'foaf:Image',
  ];
}

/**
 * Implements hook_query_alter().
 */
function $MODULE$_query_alter(Drupal\Core\Database\Query\AlterableInterface $query) {
  if ($query
    ->hasTag('micro_limit')) {
    $query
      ->range(0, 2);
  }
}

/**
 * Implements hook_query_TAG_alter().
 */
function $MODULE$_query_$TAG$_alter(Drupal\Core\Database\Query\AlterableInterface $query) {

  // This is an example of a possible hook_query_media_access_alter()
  // implementation. In other words, alter queries of media entities that
  // require access control (have the 'media_access' query tag).
  // Determine which media entities we want to remove from the query. In this
  // example, we hard-code some media IDs.
  $media_entities_to_hide = [
    1,
    3,
  ];

  // In this example, we're only interested in applying our media access
  // restrictions to SELECT queries. hook_media_access() can be used to apply
  // access control to 'update' and 'delete' operations.
  if (!$query instanceof SelectInterface) {
    return;
  }

  // The tables in the query. This can include media entity tables and other
  // tables. Tables might be joined more than once, with aliases.
  $query_tables = $query
    ->getTables();

  // The tables belonging to media entity storage.
  $table_mapping = \Drupal::entityTypeManager()
    ->getStorage('media')
    ->getTableMapping();
  $media_tables = $table_mapping
    ->getTableNames();

  // For each table in the query, if it's a media entity storage table, add a
  // condition to filter out records belonging to a media entity that we wish
  // to hide.
  foreach ($query_tables as $alias => $info) {

    // Skip over subqueries.
    if ($info['table'] instanceof SelectInterface) {
      continue;
    }
    $real_table_name = $info['table'];
    if (in_array($real_table_name, $media_tables)) {
      $query
        ->condition("{$alias}.mid", $media_entities_to_hide, 'NOT IN');
    }
  }
}

